mapper.select:

SELECT * FROM
( SELECT /*+ INDEX_DESC(TB_COMMUNITY TB_COMMUNITY_PK) */ ROWNUM RN, IDX, CUM_TITLE, CUM_CONTENTS, CUM_WRITER, CUM_REGISTER_TIME
FROM TB_COMMUNITY
WHERE IDX > 0 AND ROWNUM > 0 AND ROWNUM <= (#{pageNum, jdbcType=NUMERIC} * #{amount, jdbcType=NUMERIC})
)
WHERE RN > (#{pageNum, jdbcType=NUMERIC} -1) * #{amount, jdbcType=NUMERIC}
